import React, { Component } from 'react';
import {comment, uncomment, isAuthenciate} from '../auth/index';
import { Link } from 'react-router-dom';

class Comment extends Component {

    state = {
        text: ""
    };

    handleChange = event => {
        this.setState({ text: event.target.value})
    }

    addcomment = event => {
        event.preventDefault();
        const userId = isAuthenciate().user._id;
        const token = isAuthenciate().token;
        const postId = this.props.postId;

        comment(userId, token, postId, { text: this.state.text })
        .then(data => {
            if(data.error) {
                console.log(data.error)
            } else {
                this.setState({ text: "" })
                // dispatch fresh list of comment to parent {Singlepost}
                this.props.updateComments(data.comments);
            }
        })
    }

    render() {
        const { comments } = this.props;

        return (
            <div>
                <h2 className="mt-5 mt-5" style={{ color: "white" }}>Leave a comment</h2>

                <form onSubmit={this.addcomment}>
                    <input type="text" onChange={this.handleChange} className="form-control" />
                </form>
                {JSON.stringify(comments)}
                <hr/>
                <div className="col-md-4">
                    <h3 className="text-primary">
                        {comm.length} Followers
                    </h3>
                <hr />
                {followers.map((person, i) => (
                    <div key={i}>
                        <div>
                            <Link to={`/user/${person._id}`}>
                                <img
                                    style={{
                                        borderRadius: "50%",
                                        border: "1px solid black"
                                    }}
                                    className="float-left mr-2"
                                    height="30px"
                                    width="30px"
                                    onError={i =>
                                        (i.target.src = `${DefaultProfile}`)
                                    }
                                    src={`${
                                        process.env.REACT_APP_API_URL
                                    }/user/photo/${person._id}`}
                                    alt={person.name}
                                />
                                <div>
                                    <p className="lead">
                                        {person.name}
                                    </p>
                                </div>
                            </Link>
                        </div>
                    </div>
                ))}
            </div>
            </div>

        );
    }
}

export default Comment;
