import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { isAuthenciate } from '../auth/index';

const Sticky = () => (
    <div>
        <div id="mybutton" style={style}>
        {isAuthenciate() && (
            <Link style={btn} className="btn btn-alert" to={`/post/create`}><i className="fa fa-plus"></i></Link>
        )}
        </div>

    </div>
)


const style = {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    zIndex: "999"
}

const btn = {
    border: "none",
    color: "white",
    padding: "20px",
    textAlign: "center",
    textDecoration: "none",
    display: "inline-block",
    fontSize: "16px",
    margin: "4px 2px",
    borderRadius: "70%"
}

