import React, {Component} from 'react';
import { allpost } from '../auth/index';
import { Link } from 'react-router-dom';

class Home extends Component{

    constructor() {
        super();
        this.state = {
            post: []
        }
    }

    componentDidMount() {
        allpost().then(data => {
            if(data.error) {
                console.log(data.error);
            }else {
                this.setState({ post: data });
            }
        })              
    }

    renderPost = post => (
        <div className="col-sm-9 col-md-6 col-lg-8">
        {
            post.map((post, i) => (
                <div className="card promoting-card" key={i}>
                <div className="card-body d-flex flex-row">
                    <img src="https://mdbootstrap.com/img/Photos/Avatars/avatar-8.jpg" class="rounded-circle mr-3" height="50px" width="50px" alt="avatar" />
                    <div>
                    <h4 className="card-title font-weight-bold mb-2">{}</h4>
                    <p className="card-text"><i class="far fa-clock pr-2"></i>07/24/2018</p>
                    </div>
                </div>

                <div className="view overlay">
                    <img className="card-img-top rounded-0" src="https://mdbootstrap.com/img/Photos/Horizontal/Food/full page/2.jpg" alt="Card image cap" />
                    <a href="#!">
                    <div className="mask rgba-white-slight"></div>
                    </a>
                </div>
                <div className="card-body">
                    <div className="collapse-content">
                    <p className="card-text collapse" id="collapseContent">Recently, we added several exotic new dishes to our restaurant menu. They come from countries such as Mexico, Argentina, and Spain. Come to us, have some delicious wine and enjoy our juicy meals from around the world.</p>
                    <a className="btn btn-flat red-text p-1 my-1 mr-0 mml-1 collapsed" data-toggle="collapse" href="#collapseContent" aria-expanded="false" aria-controls="collapseContent"></a>
                    <i className="fa fa-share-alt text-muted float-right p-1 my-1" data-toggle="tooltip" data-placement="top" title="Share this post"></i>
                    <i className="fa fa-heart text-muted float-right p-1 my-1 mr-3" data-toggle="tooltip" data-placement="top" title="I like it"></i>
                    </div>
                </div>
                </div>
            ))}
        </div>
    )

    profilecard = () => (
        <div className="">
            <div className="card" style={{width:"200px"}}>
                <img className="card-img-top" src="https://timedotcom.files.wordpress.com/2015/04/512137691.jpg" alt="Card image" style={{width:"100%"}} />
                <div className="card-body">
                <h4 className="card-title">John Doe</h4>
                <p className="card-text">Some example text some example text. John Doe is an architect and engineer</p>
                <a href="#" className="btn btn-primary">See Profile</a>
                </div>
            </div>
        </div>
    );

    render() {
        const myContainer = {
            position: "relative",
            top: "100px",
            padding: "10px 10px 10px 20px"
        };

        const { post } = this.state;
        return (
            <div>
                <div className="" style={myContainer}>
                    <div className="row">
                        <div className="">
                            <div className="container" style={{ position: "relative", left: "330px" }}>
                                 { this.renderPost(post) }
                            </div>
                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
