import React, {Component} from 'react';
import { isAuthenciate, postByUser } from '../auth/index';
import { Link } from 'react-router-dom';

class PostByUser extends Component {

    constructor() {
        super();
        this.state = {
            post: [],
            error: ""
        }
    }

    componentDidMount() {
        const userId = isAuthenciate().user._id;

        postByUser(userId).then(data  => {
            if(data.error) {
                this.setState({ error: "Data not "})
            } else {
                this.setState({ post: data })
            }
        });
    }

    renderPost = post => (
        <div className="col-12 ">
        {
            post.map((post, i) => (
                <div key={i}>

                <br />
                <br />
                </div>
            ))}
        </div>
    )

    render() {
        const { post } = this.state;

        return (
            <div>
                <div className="container">
                    { this.renderPost(post) }
                </div>
            </div>
        );
    }
}

export default PostByUser;
