import React, {Component} from 'react';
import { allpost, isAuthenciate } from '../auth/index';
import { Link } from 'react-router-dom';

class Home extends Component{

    constructor() {
        super();
        this.state = {
            post: []
        }
    }

    componentDidMount() {
        allpost().then(data => {
            if(data.error) {
                console.log(data.error);
            }else {
                this.setState({ post: data });
            }
        })              
    }

    renderPost = post => (
        <div className="row">
            {
                post.map((post, i) => (
                    <div className="col-12 col-sm-8 col-md-6 col-lg-4" key={i}>
                        <div className="card">
                        <img className="card-img" src="https://s3.eu-central-1.amazonaws.com/bootstrapbaymisc/blog/24_days_bootstrap/pasta.jpg" alt="Bologna" />
                        <div className="card-img-overlay">
                        {isAuthenciate().user && isAuthenciate().user._id === user._id && (
                            <Link className="btn btn-light btn-sm" to={`/user/edit/${user._id}`}>Edit</Link>
                        )}
                            <Link to={`/user/${user._id}`} className="btn btn-light btn-sm">{post.postedBy && post.postedBy.name ? post.postedBy.name : 'Name' }</Link>
                        </div>
                        <div className="card-body">
                            <h4 className="card-title">{post.title}</h4>
                            <small className="text-muted cat">
                            </small>
                            <p className="card-text">{post.body}</p>
                            <a href="#" className="btn btn-info">Comment</a>
                        </div>
                        <div className="card-footer text-muted d-flex justify-content-between bg-transparent border-top-0">
                            <div className="views">{new Date(post.created).toDateString()}
                            </div>
                            <div className="stats">
                                <i className="fa fa-eye"></i> 1347
                                <i className="fa fa-comment"></i> 12
                            </div>
                        </div>
                    </div>
            </div>
            ))}
        </div>
    )

    render() {
        const { post } = this.state;
        return (
            <div>
                <div className="jumbotron">
                    <h1>Post</h1>
                </div>
                <div className="container">
                    { this.renderPost(post) }
                </div>
            </div>
        );
    }
}

export default Home;
