import React, {Component} from 'react';
import { isAuthenciate, postByUser } from '../auth/index';

class PostByUser extends Component {

    constructor() {
        post: []
        error: ""
    }

    componentDidMount() {
        const userId = isAuthenciate().user._id;

        postByUser(userId).then(data  => {
            if(data.error) {
                console.log(data.error);
            } else {
                this.setState({ post: data })
            }
        });
    }

    render() {
        const { post } = this.state;

        return (
            <div>
                <div className="container">
                    <div className="row">
                    {
                        <div className="col-md-6">
                            <img className="card-img-top" style={{width: 200, height:200}} src={`/post/photo/${post._id}`} onError={i => (i.target.src = 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png')} alt={user.name} />
                        </div>
                    }
                    </div>
                </div>
            </div>
        );
    }
}

export default PostByUser;
