import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { isAuthenciate, Signout } from '../auth/index';

// const isActive = (history, path) => {
//     if(history.location.pathname === path) return { color: "#1C1D21" };
//     else return { color: "#ffffff" };
// };

const Menu = ({history}) => (
    <div>
        <nav className="navbar navbar-expand-sm bg-light fixed-top" style={myNavbar}>
            <div className="container">
                <div className="navbar-brand">
                    <Link to="/"><img alt="menu" width="200" height="100" src="https://cdn2.f-cdn.com/contestentries/1295465/14376482/5ac3d4cdc34ab_thumb900.jpg" /></Link>
                </div>
                <ul className="nav justify-content-center">
                    <form className="form-inline">
                        <input className="form-control mr-sm-2" type="text" placeholder="Search" />
                        <button className="btn btn-light btn-raised" type="submit"><i className="fa fa-search" style={{ color: "black"}}></i></button>
                    </form> 
                 </ul>   
                <ul className="nav justify-content-end">
                    <li className="nav-item">
                        <Link className="nav-link" to="/user"><span><i className="fa fa-users" style={{ fontSize: "30px"}}></i></span></Link>                   
                    </li>
                    {isAuthenciate() && (
                        <>
                        <li className="nav-item">
                            <Link className="nav-link" to={`/user/${isAuthenciate().user._id}`}><span><i className="fa fa-user-o" style={{ fontSize: "30px"}}></i></span></Link>                   
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" onClick={() => Signout(() => ("/signin"))}><span><i className="fa fa-sign-out" style={{ fontSize: "30px"}}></i></span></a>                   
                        </li>
                        </>
                     )}
                    {!isAuthenciate() && (
                        <>
                        <li className="nav-item">
                            <Link className="nav-link" to="/signin">SignIN</Link>                   
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/signup">SignUP</Link>                   
                        </li>
                        </>
                     )}
                </ul>
            </div>
        </nav>
    </div>
);

const myNavbar = {
    height: "90px",
    overflow: "hidden"
};

export default withRouter(Menu);
