import React, {Component} from 'react';
import { isAuthenciate, postByUser } from '../auth/index';
import { Link } from 'react-router-dom';

class PostByUser extends Component {

    constructor() {
        super();
        this.state = {
            post: [],
            error: ""
        }
    }

    componentDidMount() {
        const userId = isAuthenciate().user._id;

        postByUser(userId).then(data  => {
            if(data.error) {
                this.setState({ error: "Data not found" })
            } else {
                this.setState({ post: data })
            }
        });
    }

    renderPost = post => (
        <div className="row">
        {
            post.map((post, i) => (
                <div className="card" key={i}>
                    <div className="card-body">
                        <Link>
                            <img className="card-img-top" style={{width: 200, height:200}}
                                 src={`/post/photo/${post._id}`} onError={i => (i.target.src = 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png')} alt={post.title} /></Link>
                    </div>
                <br />
                <br />
                </div>
            ))}
        </div>
    )

    render() {
        const { post } = this.state;

        return (
            <div>
                <div className="container">
                    { this.renderPost(post) }
                </div>
            </div>
        );
    }
}

export default PostByUser;
