import React, {Component} from 'react';
import { allpost, isAuthenciate} from '../auth/index';
import { Link } from 'react-router-dom';
import Post from './Post';

class Home extends Component{

    constructor() {
        super();
        this.state = {
            post: [],
        }
    }

    Capitalize(str){
        return str.charAt(0).toUpperCase() + str.slice(1);
    }

    componentDidMount() {
        allpost().then(data => {
            if(data.error) {
                console.log(data.error);
            }else {
                this.setState({ post: data });
            }
        });              
    }

    // componentDidUpdate(prop) {
    //     allpost().then(data => {
    //         if(data.error) {
    //             console.log(data.error);
    //         }else {
    //             this.setState({ post: data });
    //         }
    //     });
    // }

    renderPost = post => (
        <div className="col-12">
        {
            post.map((post, i) => (
                <>                
                <div className="card promoting-card" key={i}>
                    <div className="card-body d-flex flex-row">
                        <img alt="name" src={post.postedBy && post.postedBy._id ? `/user/photo/${post.postedBy._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} className="rounded-circle mr-3" height="50px" width="50px" />
                        <div style={{ marginTop: "12px" }}>
                            <Link className="btn btn-sm" to={post.postedBy && post.postedBy._id ? `/user/${post.postedBy._id}` : 'Id'}>{post.postedBy && post.postedBy.name ? post.postedBy.name : 'Name' }</Link>                        
                        </div>
                    </div>

                    <div className="view overlay">
                        <img className="card-img-top rounded-0" src={`/post/photo/${post._id}`} onError={i => (i.target.src = 'https://mdbootstrap.com/img/Photos/Horizontal/Food/full page/2.jpg')} alt="Card cap" />
                        <a href="#!">
                        <div className="mask rgba-white-slight"></div>
                        </a>
                    </div>
                    <div className="card-body">
                        <div className="collapse-content">
                            {
                                <>
                                <Link to={`/post/${post._id}`} ><span className="text-muted float-left p-1 my-1 mr-3">{post.likes.length} &nbsp;<i className="fa fa-thumbs-up" data-toggle="tooltip" title="I like it" ></i></span></Link>
                                </>
                            }
                            <p className="text-muted float-left p-1 my-1">Clcik on Like Button and </p>
                            <p className="card-text float-right p-1 my-1"><i className="far fa-clock pr-2"></i>{new Date(post.created).toDateString()}</p>
                        </div>
                    </div>
                </div>
                <br />
                <br />
                </>
            ))}
        </div>
    )

    profilecard = user => (
        <div className="col-12">
            <div className="card">
                <div className="card-header flex-row">
                    <div className="d-flex justify-content-sm-around container-fluid">
                        {isAuthenciate() && (
                            <>
                                <img src={isAuthenciate().user && isAuthenciate().user._id ? `/user/photo/${isAuthenciate().user._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} className="p-2 rounded-circle" height="50px" width="50px" alt="name" />
                            </>
                        )}
                        {isAuthenciate() && (
                            <>
                                <h4 style={{ marginTop:"7px" }} className="d-inline-block p-2 lead">{this.Capitalize(isAuthenciate().user.name)}</h4>
                            </>
                        )}
                        {isAuthenciate() && (
                            <>
                                <Link className="btn btn-primary btn-sm ml-auto p-2" to={`/user/${isAuthenciate().user._id}`}>Profile</Link>                    
                            </>
                        )}

                    </div>
                </div>
            </div>
            <br />
        </div>
    );

    Followcard = () => (
        <div className="col-12">
            <div className="card">
                <div className="card-header d-flex flex-row">
                    {isAuthenciate() && (
                        <>
                            <img alt="name " src={isAuthenciate().user && isAuthenciate().user._id ? `/user/photo/${isAuthenciate().user._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} className="rounded-circle mr-3" height="50px" width="50px" />
                        </> 
                    )}
                    <div>
                    {isAuthenciate() && (
                        <>
                            <h4 style={{ marginTop:"12px" }} >{this.Capitalize(isAuthenciate().user.name)} Follower</h4>
                        </>
                    )}
                    </div>
                </div>
                <div className="card-body flex-row" style={{ overflowY: "scroll", padding: "20px 0px 0px 0px", height: "300px"}}>
                    <div className="d-flex justify-content-sm-around container-fluid">
                        <img alt="name" className="p-2 rounded-circle" width="50" height="50" src="https://images.unsplash.com/photo-1518806118471-f28b20a1d79d?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60" />
                        <h4 style={{ marginTop:"4px" }} className="d-inline-block p-2 lead">Subhash Kalarathi</h4>
                        <button className="btn btn-primary btn-sm ml-auto p-2">UnFollow</button>                    
                    </div>
                    <br />
                    <div className="d-flex justify-content-sm-around container-fluid">
                        <img alt="name" className="p-2 rounded-circle" width="50" height="50" src="https://images.unsplash.com/photo-1518806118471-f28b20a1d79d?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60" />
                        <h4 style={{ marginTop:"4px" }} className="d-inline-block p-2 lead">Subhash Kalarathi</h4>
                        <button className="btn btn-primary btn-sm ml-auto p-2">UnFollow</button>                    
                    </div>
                </div>
            </div>
        </div>
    );

    render() {
        const myContainer = {
            position: "relative",
            top: "110px",
        };

        const fixed = {
            position: "fixed",
        };

        const { post } = this.state;
        return (
            <div>
                <div className="container-fluid" style={myContainer}>
                    <div className="row">
                        <div className="col-8">
                            <div className="row">
                                <div className="col-12">
                                    <Post />
                                    {this.renderPost(post)}
                                </div>
                            </div>
                        </div>
                        <div className="col-4">
                            <div className="fixed col-4" style={fixed}>
                                {this.profilecard()}                    
                                {this.Followcard()}          
                            </div>                  
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
