import React, { Component } from 'react';

class Comment extends Component {

    state = {
        text: ""
    }

    handleChange = event => {
        this.setState({ text: event.target.value})
    }

    render() {
        return (
            <div>
                <h2 className="mt-5 mt-5">Leave a comment</h2>

                <form>
                    <input type="text" onChange={this.handleChange} value={text} />
                </form>
            </div>
        );
    }
}
