import React, { Component } from 'react';
import { isAuthenciate, read } from '../auth/index';
import { Redirect, Link } from 'react-router-dom';

class Profile extends Component {
    constructor() {
        super();
        this.state = {
            user: "",
            redirectToSigin: false,
            users: { following: [], followers: [] },
            following: false,
        }
    }

    init = userId => {
        const token = isAuthenciate().token;
        read(userId, token)
        .then(data => {
            if(data.error) {
                this.setState({ redirectToSigin: true })
            }else {
                this.setState({ user: data })
            }
        });
    }

    componentDidMount() {
        const userId = this.props.match.params.userId;
        this.init(userId);
    }

    componentWillReceiveProps(props) {
        const userId = props.match.params.userId;
        this.init(userId);
    }

    render() {
        const { redirectToSigin, user} = this.state;
             if(redirectToSigin) return <Redirect to="/signin" />

        return(
            <div className="container">
                <h2 className="mt-5 mb-5" style={{color:"white"}}>Profile</h2>
                <div className="row">
                    <div className="col-md-6">
                        <img className="card-img-top" style={{width: 200, height:200}} src={`/user/photo/${user._id}`} onError={i => (i.target.src = 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png')} alt={user.name} />
                    </div>
                    <div className="col-md-6">
                        <div className="lead">
                            <p style={{ color: "white"}}>Hello, {user.name}</p>
                            <p style={{ color: "white"}}>Email : {user.email}</p>
                            <p style={{ color: "white"}}>{`Joined : ${new Date(user.created).toDateString()}`}</p>
                        </div>
                        {isAuthenciate().user && isAuthenciate().user._id === user._id && (
                            <div className="d-inline-block">
                                <Link className="btn btn-raised btn-primary mr-5" to={`/user/edit/${user._id}`}>Edit</Link>
                            </div>
                        )}
                        {isAuthenciate().user && isAuthenciate().user._id === user._id && (
                            <div className="d-inline-block">
                                <Link className="btn btn-raised btn-info mr-5" to={`/user/post/${user._id}`}>Create Post</Link>
                            </div>
                        )}
                        {isAuthenciate().user && isAuthenciate().user._id === user._id && (
                            <div className="d-inline-block">
                                <Link className="btn btn-raised btn-danger mr-5" to={`/user/edit/${user._id}`}>Delete</Link>
                            </div>
                        )}
                    </div>
                </div>                
            </div>
        );
    }
}

export default Profile;
