import React, {Component} from 'react';
import { allpost } from '../auth/index';
import { Link } from 'react-router-dom';

class Home extends Component{

    constructor() {
        super();
        this.state = {
            post: []
        }
    }

    componentDidMount() {
        allpost().then(data => {
            if(data.error) {
                console.log(data.error);
            }else {
                this.setState({ post: data });
            }
        })              
    }

    renderPost = post => (
        <div className="">
            {
                post.map((post, i) => (
                    <div className="col-12 col-sm-8 col-md-6 col-lg-4" key={i}>
                        <div className="padding">
                        <div className="card">
                        <img className="card-img" width="250" height="200" src={`post/photo/${post._id}`} onError={i => (i.target.src = "https://timedotcom.files.wordpress.com/2015/04/512137691.jpg")} alt={post.postedBy && post.postedBy.name ? post.postedBy.name : 'Id'} />
                        <div className="card-img-overlay">
                            <Link className="btn btn-sm" to={post.postedBy && post.postedBy._id ? `/user/${post.postedBy._id}` : 'Id'}>{post.postedBy && post.postedBy.name ? post.postedBy.name : 'Name' }</Link>                        
                        </div>
                        <div className="card-body">
                            <h6 className="card-title">{post.title}</h6>
                            <p className="card-text">{post.body}</p>
                        </div>
                        <div className="card-footer text-muted d-flex justify-content-between bg-transparent border-top-0">
                            <div className="views">{new Date(post.created).toDateString()}
                            </div>
                            <div className="stats">
                                <button className="btn btn-info"><i className="fa fa-thumbs-up"></i>&nbsp;12</button>
                                <button className="btn btn-info"><i className="fa fa-comment"></i>&nbsp;12</button>
                            </div>
                        </div>
                    </div>
                    </div>
            </div>
            ))}
        </div>
    )

    showFollower = () => (
        <div className="">
            <div>
                <h1>Follower</h1>
            </div>
        </div>
    );

    render() {
        const { post } = this.state;
        return (
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-sm-3 col-md-6 col-lg-4">
                            { this.showFollower() }
                        </div>
                        <div className="col-lg-9 col-mlg-6 col-lg-8">
                            { this.renderPost(post) }
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

const myContainer = {

}

export default Home;
