import React, {Component} from 'react';
import { allpost, isAuthenciate, read } from '../auth/index';
import { Link } from 'react-router-dom';

class Home extends Component{

    constructor() {
        super();
        this.state = {
            post: []
        }
    }

    Capitalize(str){
        return str.charAt(0).toUpperCase() + str.slice(1);
    }

    componentDidMount() {
        allpost().then(data => {
            if(data.error) {
                console.log(data.error);
            }else {
                this.setState({ post: data });
            }
        });              
    }

    renderPost = post => (
        <div className="col-12">
        {
            post.map((post, i) => (
                <div>                
                <div className="card promoting-card" key={i}>
                    <div className="card-body d-flex flex-row">
                        <img src={post.postedBy && post.postedBy._id ? `/user/photo/${post.postedBy._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} class="rounded-circle mr-3" height="50px" width="50px" />
                        <div>
                            <Link className="btn btn-sm" to={post.postedBy && post.postedBy._id ? `/user/${post.postedBy._id}` : 'Id'}>{post.postedBy && post.postedBy.name ? post.postedBy.name : 'Name' }</Link>                        
                            <p className="card-text"><i class="far fa-clock pr-2"></i>07/24/2018</p>
                        </div>
                    </div>

                    <div className="view overlay">
                        <img className="card-img-top rounded-0" src={`/post/photo/${post._id}`} onError={i => (i.target.src = 'https://mdbootstrap.com/img/Photos/Horizontal/Food/full page/2.jpg')} alt="Card image cap" />
                        <a href="#!">
                        <div className="mask rgba-white-slight"></div>
                        </a>
                    </div>
                    <div className="card-body">
                        <div className="collapse-content">
                            <span className="text-muted float-left p-1 my-1 mr-3">12 &nbsp;<i className="fa fa-heart" data-toggle="tooltip" title="I like it" ></i></span>
                            <span className="text-muted float-left p-1 my-1"><i className="fa fa-share-alt" data-toggle="tooltip" title="Share this post"></i></span>
                        </div>
                    </div>
                    <div className="card-footer">
                        <form>
                            <div class="input-group mb-3">
                                <textarea class="form-control" rows="1" id="comment" placeholder="Comment:"></textarea>
                                <div class="input-group-append">
                                    <button class="btn btn-success btn-raised" type="submit">Go</button>
                                </div>
                            </div>
                        </form>
                    </div>
                </div>
                <br />
                <br />
                <br />
                <br />
                </div>
            ))}
        </div>
    )

    profilecard = user => (
        <div className="col-12">
            <div className="card">
                <div className="card-body d-flex flex-row">
                    <img src={isAuthenciate().user && isAuthenciate().user._id ? `/user/photo/${isAuthenciate().user._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} class="rounded-circle mr-3" height="50px" width="50px" />
                    <div>
                        <div className="d-flex">
                            <h4 className="lead">{this.Capitalize(isAuthenciate().user.name)}</h4>
                            <button className="btn btn-success btn-raised">Submit</button>                    
                    </div>                
                    </div>
                </div>
            </div>
            <br />
        </div>
    );

    Followcard = () => (
        <div className="col-12">
            <div className="card">
                <div className="card-header d-flex flex-row">
                    <img src={isAuthenciate().user && isAuthenciate().user._id ? `/user/photo/${isAuthenciate().user._id}` : 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png'} class="rounded-circle mr-3" height="50px" width="50px" />
                    <div>
                        <h1></h1>
                        <h1></h1>
                        <h4>{this.Capitalize(isAuthenciate().user.name)} Follower</h4>
                    </div>
                </div>
                <div className="card-body flex-row" style={{ overflowY: "scroll", padding: "20px 0px 0px 60px", height: "300px"}}>
                    <div className="d-flex justify-content-around">
                        <h4 className="d-inline-block center-block align-items-start">Subhash</h4>
                        <button className="btn btn-primary btn-raised">UnFollow</button>                    
                    </div>
                </div>
            </div>
        </div>
    );

    render() {
        const myContainer = {
            position: "relative",
            top: "110px",
        };

        const fixed = {
            position: "fixed",
        };

        const { post } = this.state;
        return (
            <div>
                <div className="container-fluid" style={myContainer}>
                    <div className="row">
                        <div className="col-8">
                            <div className="row">
                                <div className="col-12">
                                    {this.renderPost(post)}
                                </div>
                            </div>
                        </div>
                        <div className="col-4">
                            <div className="fixed col-4" style={fixed}>
                                {this.profilecard()}                    
                                {this.Followcard()}          
                                </div>                  
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
