import React, { Component } from 'react';
import { post, isAuthenciate } from '../auth/index';
import { Redirect } from 'react-router-dom';
import './post.css';

class Post extends Component {

    constructor() {
        super();
        this.state = {
            title: "",
            body: "",
            error: "",
            redirecttopage: false
        }
    }

    componentDidMount() {
        this.userData = new FormData();
    }

    handleChange = title => event => {
        const value = title === 'photo' ? event.target.files[0] : event.target.value;
        this.setState({error: ""});
        this.setState({[title]: value});
    }

    clickSubmit = event => {
        event.preventDefault();
        const userId = isAuthenciate().user._id;
        const tokens = isAuthenciate().token;
        post(this.userData, userId, tokens).then(data => {
            if(data.error) {
                this.setState({ error: "" });
            }else {
                this.setState({ redirecttopage: true });
            }
        })
    }

    postForm = (title, body) => (
        <form>
            <div className="form-group">
                <label className="text-muted">Photo</label>
                <input type="file" onChange={this.handleChange("photo")} className="form-control" />
            </div>
            <div className="form-group">
                <label className="text-muted">Title</label>
                <input type="text" onChange={this.handleChange("title")} className="form-control" value={title}/>
            </div>
            <div className="form-group">
                <label className="text-muted">Body</label>
                <input type="email" onChange={this.handleChange("body")} className="form-control" value={body}/>
            </div>
            <div className="form-group">
                <button onClick={this.clickSubmit} className="btn btn-raised btn-primary">Submit</button>
            </div>
        </form>
    );

    render() {
        const { title, body,redirecttopage } = this.state;

        if(redirecttopage) {
            return <Redirect />
        }

        return (
            <div>
                <div  className="jumbotron">
                    <h1>Post</h1>
                </div>
                <div className="container">
                    {this.postForm(title, body)}
                </div>
            </div>
        );
    }
}

export default Post;
