import React from 'react';
import { Link, withRouter } from 'react-router-dom';
import { isAuthenciate, Signout } from '../auth/index';

// const isActive = (history, path) => {
//     if(history.location.pathname === path) return { color: "#1C1D21" };
//     else return { color: "#ffffff" };
// };

const Menu = ({history}) => (
    <div>
        <ul className="nav nav-tabs bg-primary">
            <li className="nav-item">
                <Link className="nav-link"  to="/">Home</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" style={isActive(history, "/user")}  to="/user">User</Link>
            </li>
            <li className="nav-item">
                <Link className="nav-link" style={isActive(history, "/search")}  to="/search"><small>Search</small></Link>
            </li>
            { isAuthenciate() && (
                <>
                <li className="nav-item">
                    <Link className="nav-link" to={`/user/${isAuthenciate().user._id}`} 
                    style={isActive(history, `/user/${isAuthenciate().user._id}`)} ><img width="25" height="25"
                    src={`/user/photo/${isAuthenciate().user._id}`} onError={i => (i.target.src = 'https://i.stack.imgur.com/l60Hf.pnghttps://i.stack.imgur.com/l60Hf.png')}
                    class="rounded-circle" alt={isAuthenciate().user.name} /> &nbsp; <small><b>{isAuthenciate().user.name }</b></small></Link>
                </li>
                <li className="nav-item">
                    <a className="nav-link" href="#" style={isActive(history, "/signout")} 
                            onClick={() => Signout(() => history.push('/signin'))}>
                    Signout
                    </a>
                </li>
                </>
            )}
            { !isAuthenciate()  && (
                <>
                <li className="nav-item">
                    <Link className="nav-link" style={isActive(history, "/signin")} to="/signin">Signin</Link>
                </li>
                <li className="nav-item">
                    <Link className="nav-link" style={isActive(history, "/signup")} to="/signup">Signup</Link>
                </li>
                </>
            )}
        </ul>
    </div>
);

export default withRouter(Menu);
